package Hello;

public class forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//even numbers
		int n=1;
		
		while(n<=50) {
			if(n%2==0)
			
			System.out.println(n);
			n++;
			
		}
//odd numbers.
		int i=0;
		while(i<=50) {
			if(i%2!=0)
			
			System.out.println(i);
			i++;
			
		
		

	}
		
		

}
}
